import React from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import useToken from '../../components/App/useToken';
import Login from '../../components/Login/Login';


async function getBlogs() {
   return fetch('http://localhost:8000/api/blogs', {
     method: 'POST',
     headers: {
       'Content-Type': 'application/json'
     },
     body: JSON.stringify()
   })
     .then(data => data)
  }

export default function Dashboard() {

  const { token, setToken, logout } = useToken();

  const [productsArray, setProductsArray] = useState([]);

   const getproducts = () => {

      return fetch('http://localhost:8000/api/blogs', {
         method: 'POST',
         headers: {
           'Content-Type': 'application/json'
         },
         body: JSON.stringify()
      })
      .then((data) => {
          setProductsArray(data.map((item) => {
              return { ...item.data(), id: item.id }
          }));

          console.log(productsArray);
      })
       
    }

    useEffect(() => {
        getproducts();
    }, [])

  if (!token) {
    return <Login setToken={setToken} />;
  }

 

  return(
    <div>
    <h2>Listado de Noticias del Cliente </h2>

    {productsArray.map((prod) => {
          return (
              <div>
              <p class="px-4 py-3">{prod.id}</p>
              <p class="px-4 py-3">{prod.titulo}</p>
              <p class="px-4 py-3">{prod.contenido}</p>
              </div>
          )
      })}

    </div>

  );
}
